{% if collection.metafields.custom.seo_metadata != blank %}
  <div class="collection-seo-wrapper">
    <div class="collection-seo-container">
      <div class="collection-seo-metadata collapsed" id="seoContent">
        {{ collection.metafields.custom.seo_metadata | metafield_tag }}
      </div>
      <div class="seo-fade-overlay" id="seoFadeOverlay"></div>
      <button class="seo-toggle-button" id="seoToggleButton" aria-expanded="false">
        <span>Read more</span>
        <svg class="toggle-icon" width="14" height="14" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M4 6L8 10L12 6" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
        </svg>
      </button>
    </div>
  </div>
{% endif %}

<style>
/* Optimized SEO Section - Subtle & Responsive */
.collection-seo-wrapper {
  width: 100%;
  margin: 40px auto 60px;
  padding: 0 20px;
}

.collection-seo-container {
  position: relative;
  background: transparent;
}

.collection-seo-metadata {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  color: #666;
  line-height: 1.6;
  overflow: hidden;
  transition: max-height 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  padding: 20px 0;
}

.collection-seo-metadata.collapsed {
  max-height: 180px;
  position: relative;
}

.collection-seo-metadata.collapsed::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 40px;
  pointer-events: none;
}

.collection-seo-metadata.expanded {
  max-height: 5000px;
}

/* Fade overlay - removed for cleaner look */
.seo-fade-overlay {
  display: none;
}

/* Typography - Subtle & Small */
.collection-seo-metadata h1 {
  font-size: 20px;
  font-weight: 600;
  color: #333;
  margin: 0 0 14px 0;
  line-height: 1.4;
  letter-spacing: -0.01em;
}

.collection-seo-metadata h2 {
  font-size: 17px;
  font-weight: 600;
  color: #444;
  margin: 24px 0 12px 0;
  line-height: 1.4;
}

.collection-seo-metadata h3 {
  font-size: 15px;
  font-weight: 600;
  color: #555;
  margin: 20px 0 10px 0;
  line-height: 1.4;
}

.collection-seo-metadata p {
  font-size: 13px;
  color: #666;
  margin: 0 0 12px 0;
  line-height: 1.6;
}

.collection-seo-metadata p:last-child {
  margin-bottom: 0;
}

.collection-seo-metadata ul,
.collection-seo-metadata ol {
  margin: 0 0 12px 0;
  padding-left: 20px;
}

.collection-seo-metadata li {
  font-size: 13px;
  color: #666;
  margin-bottom: 6px;
  line-height: 1.6;
}

.collection-seo-metadata a {
  color: #555;
  text-decoration: none;
  border-bottom: 1px solid transparent;
  transition: all 0.2s ease;
}

.collection-seo-metadata a:hover {
  color: #333;
  border-bottom-color: #999;
}

.collection-seo-metadata strong {
  font-weight: 600;
  color: #444;
}

/* Subtle toggle button */
.seo-toggle-button {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  background: none;
  border: none;
  padding: 10px 0;
  margin-top: 12px;
  font-size: 13px;
  font-family: inherit;
  font-weight: 500;
  color: #777;
  cursor: pointer;
  transition: all 0.2s ease;
}

.seo-toggle-button:hover {
  color: #333;
}

.seo-toggle-button:focus {
  outline: none;
}

.toggle-icon {
  transition: transform 0.3s ease;
  opacity: 0.7;
}

.seo-toggle-button[aria-expanded="true"] .toggle-icon {
  transform: rotate(180deg);
}

/* Tablet - 768px to 1024px */
@media (max-width: 1024px) {
  .collection-seo-wrapper {
    margin: 32px auto 48px;
    padding: 0 16px;
  }

  .collection-seo-metadata {
    padding: 16px 0;
  }

  .collection-seo-metadata.collapsed {
    max-height: 160px;
  }

  .collection-seo-metadata h1 {
    font-size: 18px;
    margin-bottom: 12px;
  }

  .collection-seo-metadata h2 {
    font-size: 16px;
    margin: 20px 0 10px 0;
  }

  .collection-seo-metadata h3 {
    font-size: 14px;
  }

  .collection-seo-metadata p,
  .collection-seo-metadata li {
    font-size: 12px;
  }
}

/* Mobile - Up to 767px */
@media (max-width: 767px) {
  .collection-seo-wrapper {
    margin: 24px auto 40px;
    padding: 0 16px;
  }

  .collection-seo-metadata {
    padding: 12px 0;
  }

  .collection-seo-metadata.collapsed {
    max-height: 140px;
  }

  .seo-fade-overlay {
    bottom: 44px;
    height: 60px;
  }

  .collection-seo-metadata h1 {
    font-size: 17px;
    margin-bottom: 10px;
    line-height: 1.35;
  }

  .collection-seo-metadata h2 {
    font-size: 15px;
    margin: 16px 0 8px 0;
  }

  .collection-seo-metadata h3 {
    font-size: 14px;
    margin: 14px 0 8px 0;
  }

  .collection-seo-metadata p,
  .collection-seo-metadata li {
    font-size: 12px;
    line-height: 1.55;
    margin-bottom: 10px;
  }

  .collection-seo-metadata ul,
  .collection-seo-metadata ol {
    padding-left: 18px;
    margin-bottom: 10px;
  }

  .collection-seo-metadata li {
    margin-bottom: 5px;
  }

  .seo-toggle-button {
    padding: 12px 0;
    margin-top: 10px;
    font-size: 12px;
    min-height: 40px;
    gap: 5px;
  }

  .toggle-icon {
    width: 12px;
    height: 12px;
  }
}

/* Small Mobile - Up to 480px */
@media (max-width: 480px) {
  .collection-seo-wrapper {
    padding: 0 12px;
    margin: 20px auto 32px;
  }

  .collection-seo-metadata.collapsed {
    max-height: 130px;
  }

  .collection-seo-metadata h1 {
    font-size: 16px;
    margin-bottom: 8px;
  }

  .collection-seo-metadata h2 {
    font-size: 14px;
  }

  .collection-seo-metadata p,
  .collection-seo-metadata li {
    font-size: 11px;
  }
}

/* Accessibility */
@media (prefers-reduced-motion: reduce) {
  .collection-seo-metadata,
  .seo-fade-overlay,
  .toggle-icon,
  .collection-seo-metadata a {
    transition: none;
  }
}

/* Print - Show all content */
@media print {
  .collection-seo-metadata.collapsed {
    max-height: none !important;
  }

  .seo-toggle-button,
  .seo-fade-overlay {
    display: none !important;
  }
}
</style>

<script>
(function() {
  'use strict';

  document.addEventListener('DOMContentLoaded', function() {
    const content = document.getElementById('seoContent');
    const toggleBtn = document.getElementById('seoToggleButton');
    const fadeOverlay = document.getElementById('seoFadeOverlay');

    if (!content || !toggleBtn || !fadeOverlay) return;

    // Check if content needs toggle button
    function checkContentHeight() {
      const contentHeight = content.scrollHeight;
      const collapsedHeight = parseInt(window.getComputedStyle(content).maxHeight);

      if (contentHeight <= collapsedHeight) {
        toggleBtn.style.display = 'none';
        fadeOverlay.style.display = 'none';
        content.classList.remove('collapsed');
      } else {
        toggleBtn.style.display = 'inline-flex';
        fadeOverlay.style.display = 'block';
      }
    }

    // Toggle functionality
    toggleBtn.addEventListener('click', function() {
      const isCollapsed = content.classList.contains('collapsed');

      if (isCollapsed) {
        content.classList.remove('collapsed');
        content.classList.add('expanded');
        toggleBtn.setAttribute('aria-expanded', 'true');
        toggleBtn.querySelector('span').textContent = 'Read less';
        fadeOverlay.classList.add('hidden');
      } else {
        content.classList.add('collapsed');
        content.classList.remove('expanded');
        toggleBtn.setAttribute('aria-expanded', 'false');
        toggleBtn.querySelector('span').textContent = 'Read more';
        fadeOverlay.classList.remove('hidden');

        // Smooth scroll back to top of content
        content.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
      }
    });

    // Initial check
    checkContentHeight();

    // Debounced resize handler
    let resizeTimer;
    window.addEventListener('resize', function() {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(checkContentHeight, 250);
    });
  });
})();
</script>
